import "../Styles/styles.css";
import "bootstrap/dist/css/bootstrap.min.css";
import fruit from "../Assets/Juneplumspic4.jpeg";
import { useState, useEffect } from "react";

export const RecentPurch = () => {
  let [notHiddenClass, setNotHiddenClass] = useState("recentmain-hidden");

  let classEdit = () => {
    setNotHiddenClass("recentmain");
  };
  setTimeout(classEdit, 8000);
  // setTimeout(setNotHiddenClass('recentmain-hidden'), 10000)

  const purchases = [
    {
      img: fruit,
      stars: "★★★★★",
      name: "Douglas from Miami, FL.",
      item: "Recently bought 5LBS of June Plums",
      qty: "Qty:1",
      purchased: " an hour ago",
    },
    {
      img: fruit,
      stars: "★★★★★",
      name: "Yaira from Miami, FL.",
      item: "Recently bought 5LBS of June Plums",
      qty: "Qty:2",
      purchased: "3 hours ago",
    },
    {
      img: fruit,
      stars: "★★★★★",
      name: "Samantha from Miami, FL.",
      item: "Recently bought 5LBS of June Plums",
      qty: "Qty:1",
      purchased: "11 hours ago",
    },
  ];

  // let finish = () => {
  //   setTimeout(setNotHiddenClass('recentmain-hidden'),2000)
  // }

  useEffect(() => {
    function arrPlusDelay(array, delegate, delay) {
      array.forEach(function(el, i) {
        setTimeout(function() {
          setTimeout(()=>{
            setNotHiddenClass('recwnt')
          },5000)
          delegate(array[i]);
        }, i * delay);
      });
    }

    arrPlusDelay(
      purchases,
      function(obj) {
        let create = document.getElementById("recentmain");
        create.innerHTML = `<img src=${obj.img} alt="juneplums" />
              <div className='spandiv'>
                <span className='purchinfo' styles>${obj.stars}</span>
                <br />
                <span className='purchinfo'>${obj.name}</span>
                <br />
                <span className='purchinfo'>
                  ${obj.item} ${obj.qty}
                </span>
                <br />
                <span className='purchinfo'>${obj.purchased}</span>
                <br />
                </div>`;
      },
      8000
    );
  });

  // ________________works but static _________________________
  // let toaster = () => {
  //       for (let data of purchases) {
  //         console.log(data)
  //           return (
  //           <div className="recentmain" key={Math.random() * 1000}>
  //             <img src={fruit} alt="juneplums" />
  //             <div className="info">
  //               <span>{data.stars}</span>
  //               <br />
  //               <span>{data.name}</span>
  //               <br />
  //               <span>
  //                 {data.item} {data.qty}
  //               </span>
  //               <br />
  //               <span>{data.purchased}</span>
  //               <br />
  //             </div>
  //           </div>
  //         )

  //       }
  //     }
  // ___________________________________

  return (
    <>
      <div className={notHiddenClass} id="recentmain"></div>
    </>
  );
};
