import "../Styles/styles.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { useState } from "react";
import emailjs from "emailjs-com";



export const DiscountForm = () => {
  let [firstName, setFirstName] = useState("");
  let [lastName, setLastName] = useState("");
  let [email, setEmail] = useState("");
  let [code, setCode] = useState("");
  let [to_Name, setTo_Name] = useState("myredlandsfarm@gmail.com");

  let REACT_APP_serviceId = "service_p2o5q5k";
  let REACT_APP_publicKey = "zht3eAmzh8NE7Y7PJ";
  let REACT_APP_templateId = "template_rjz440d";

  let handleSubmit = (e) => {
    e.preventDefault();
    console.log(firstName);
    console.log(lastName);
    console.log(email);
    console.log("Code Given");

    let params = {
      to_Name: to_Name,
      firstName: firstName,
      lastName: lastName,
      email: email,
    };
    emailjs
      .send(
        REACT_APP_serviceId,
        REACT_APP_templateId,
        params,
        REACT_APP_publicKey
      )
      .then((res) => {
        alert("Email sent successfully!");
      })
      .catch((error) => {
        console.log(error);
      });
    let getClass = document.getElementById("code");
    getClass.className = "discountcodeshow";
    return code;
  };

  return (
    <>
      <div className="container-fluid">
        <form onSubmit={handleSubmit} className="form-horizontal">
          <div className="label">
            <label htmlFor="firstName">*First Name:</label>
          </div>
          <div className="input">
            <input
              type="text"
              id="first name"
              onChange={(e) => setFirstName(e.target.value)}
              required
            />
          </div>

          <div className="label">
            <label htmlFor="lastName">*Last Name:</label>
          </div>
          <div className="input">
            <input
              type="text"
              id="lastName"
              onChange={(e) => setLastName(e.target.value)}
              required
            />
          </div>

          <div className="label">
            <label htmlFor="email">*Email:</label>
          </div>
          <div className="input">
            <input
              type="email"
              id="email"
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div>
            <input type="submit" className="submit" value="Submit" />
          </div>
          <div className="discountcode" id="code">
            <h1 className="code" id="code">
              <a
                href="https://myredlandsfarm.etsy.com?coupon=LOYALTY15OFF"
                target="_blank"
                rel="noreferrer"
              >
                LOYALTY15OFF
              </a>
              {code}
            </h1>
          </div>
        </form>
      </div>
    </>
  );
};
